SELECT NVL(V0.TOT,0) vaEmitido, NVL(V1.SUM,0) , NVL(V1.TOT,0) vaTicket, NVL(V2.SUM,0) , NVL(V2.TOT,0) vaDevolucion, NVL(V3.SUM,0) , NVL(V3.TOT,0) vaFactura
FROM (SELECT SUM(CANT) SUM, SUM(TOT) TOT FROM(SELECT COUNT(PED_VEN.NU_PEDIDO) CANT, SUM(PED_VEN.VA_TOTAL_PRECIO_VENTA) TOT FROM VTTC_PEDIDO_VENTA PED_VEN
1 = 1
AND PED_VEN.CO_COMPANIA = #{localId.coCompania}
AND PED_VEN.CO_LOCAL = #{localId.coLocal}
AND PED_VEN.FE_PEDIDO BETWEEN TO_DATE(TO_CHAR(#{feInicio},'dd/MM/yyyy') || ' 00:00:00','dd/MM/yyyy HH24:MI:SS') AND TO_DATE(TO_CHAR(#{feFin},'dd/MM/yyyy') || ' 23:59:59','dd/MM/yyyy HH24:MI:SS')
AND PED_VEN.ES_PEDIDO_VENTA='C' )) V0, (SELECT SUM(SUM) SUM, SUM(TOT) TOT FROM(SELECT COUNT(PED_VEN.NU_PEDIDO) SUM, SUM(PED_VEN.VA_TOTAL_PRECIO_VENTA) TOT FROM VTTV_COMPROBANTE_PAGO PED_VEN
1 = 1
AND PED_VEN.CO_COMPANIA = #{localId.coCompania}
AND PED_VEN.CO_LOCAL = #{localId.coLocal}
AND PED_VEN.FE_COMPROBANTE BETWEEN TO_DATE(TO_CHAR(#{feInicio},'dd/MM/yyyy') || ' 00:00:00','dd/MM/yyyy HH24:MI:SS') AND TO_DATE(TO_CHAR(#{feFin},'dd/MM/yyyy') || ' 23:59:59','dd/MM/yyyy HH24:MI:SS')
AND PED_VEN.TI_COMPROBANTE IN ('01','05', '09')
)) V1, (SELECT SUM(SUM) SUM, SUM(TOT) TOT FROM(SELECT COUNT(PED_VEN.NU_PEDIDO) SUM, SUM(PED_VEN.VA_TOTAL_PRECIO_VENTA) TOT FROM VTTV_COMPROBANTE_PAGO PED_VEN
1 = 1
AND PED_VEN.CO_COMPANIA = #{localId.coCompania}
AND PED_VEN.CO_LOCAL = #{localId.coLocal}
AND PED_VEN.FE_ANULA_COMPROBANTE BETWEEN TO_DATE(TO_CHAR(#{feInicio},'dd/MM/yyyy') || ' 00:00:00','dd/MM/yyyy HH24:MI:SS') AND TO_DATE(TO_CHAR(#{feFin},'dd/MM/yyyy') || ' 23:59:59','dd/MM/yyyy HH24:MI:SS')
AND PED_VEN.TI_COMPROBANTE IN ('01','02','05','06','09','10','14')
)) V2, ( SELECT SUM(SUM) SUM, SUM(TOT) TOT FROM( SELECT COUNT(PED_VEN.NU_PEDIDO) SUM, SUM(PED_VEN.VA_TOTAL_PRECIO_VENTA) TOT FROM VTTV_COMPROBANTE_PAGO PED_VEN
1 = 1
AND PED_VEN.CO_COMPANIA = #{localId.coCompania}
AND PED_VEN.CO_LOCAL = #{localId.coLocal}
AND PED_VEN.FE_COMPROBANTE BETWEEN TO_DATE(TO_CHAR(#{feInicio},'dd/MM/yyyy') || ' 00:00:00','dd/MM/yyyy HH24:MI:SS') AND TO_DATE(TO_CHAR(#{feFin},'dd/MM/yyyy') || ' 23:59:59','dd/MM/yyyy HH24:MI:SS')
AND PED_VEN.TI_COMPROBANTE IN ('02','06','10')
) )V3, (SELECT SUM(SUM) SUM, SUM(TOT) TOT FROM (SELECT COUNT(PED_VEN.NU_PEDIDO) SUM, SUM(PED_VEN.VA_TOTAL_PRECIO_VENTA) TOT FROM VTTV_COMPROBANTE_PAGO PED_VEN
1 = 1
AND PED_VEN.CO_COMPANIA = #{localId.coCompania}
AND PED_VEN.CO_LOCAL = #{localId.coLocal}
AND PED_VEN.FE_COMPROBANTE BETWEEN TO_DATE(TO_CHAR(#{feInicio},'dd/MM/yyyy') || ' 00:00:00','dd/MM/yyyy HH24:MI:SS') AND TO_DATE(TO_CHAR(#{feFin},'dd/MM/yyyy') || ' 23:59:59','dd/MM/yyyy HH24:MI:SS')
AND PED_VEN.TI_COMPROBANTE IN ('04', '11','12')
)) V9, (SELECT SUM(SUM) SUM, SUM(TOT) TOT FROM (SELECT COUNT(PED_VEN.NU_PEDIDO) SUM, SUM(PED_VEN.VA_TOTAL_PRECIO_VENTA) TOT FROM VTTV_COMPROBANTE_PAGO PED_VEN
1 = 1
AND PED_VEN.CO_COMPANIA = #{localId.coCompania}
AND PED_VEN.CO_LOCAL = #{localId.coLocal}
AND PED_VEN.FE_ANULA_COMPROBANTE BETWEEN TO_DATE(TO_CHAR(#{feInicio},'dd/MM/yyyy') || ' 00:00:00','dd/MM/yyyy HH24:MI:SS') AND TO_DATE(TO_CHAR(#{feFin},'dd/MM/yyyy') || ' 23:59:59','dd/MM/yyyy HH24:MI:SS')
AND PED_VEN.TI_COMPROBANTE IN ('04', '11','12')
)) V10, (SELECT COUNT(PED_VEN.NU_PEDIDO) SUM, SUM(PED_VEN.VA_TOTAL_PRECIO_VENTA) TOT FROM VTTC_PEDIDO_VENTA PED_VEN
1 = 1
AND PED_VEN.CO_COMPANIA = #{localId.coCompania}
AND PED_VEN.CO_LOCAL = #{localId.coLocal}
AND PED_VEN.FE_PEDIDO BETWEEN TO_DATE(TO_CHAR(#{feInicio},'dd/MM/yyyy') || ' 00:00:00','dd/MM/yyyy HH24:MI:SS') AND TO_DATE(TO_CHAR(#{feFin},'dd/MM/yyyy') || ' 23:59:59','dd/MM/yyyy HH24:MI:SS')
AND PED_VEN.TI_PEDIDO = '7' AND PED_VEN.CA_ITEM = 0 ) v11

SELECT Z.CO_FORMA_PAGO coFormaPago , Z.DE_CORTA_FORMA_PAGO deFormaPago , TOTAL vaFormaPago FROM ( SELECT TO_CHAR(NVL(FOR_PAG.CO_FORMA_PAGO,' ')) CO_FORMA_PAGO, TO_CHAR(NVL(FOR_PAG.DE_CORTA_FORMA_PAGO,' ')) DE_CORTA_FORMA_PAGO, SUM(NVL(PAG_PED.IM_TOTAL_PAGO,0)-NVL(PAG_PED.VA_VUELTO,0)) TOTAL FROM VTTC_PEDIDO_VENTA PED_VEN, VTTX_FORMA_PAGO_PEDIDO PAG_PED, VTTR_FORMA_PAGO FOR_PAG
1 = 1
AND PED_VEN.CO_COMPANIA = #{localId.coCompania}
AND PED_VEN.CO_LOCAL = #{localId.coLocal}
AND PED_VEN.FE_PEDIDO BETWEEN TO_DATE(TO_CHAR(#{feInicio},'dd/MM/yyyy') || ' 00:00:00','dd/MM/yyyy HH24:MI:SS') AND TO_DATE(TO_CHAR(#{feFin},'dd/MM/yyyy') || ' 23:59:59','dd/MM/yyyy HH24:MI:SS')
AND PED_VEN.ES_PEDIDO_VENTA ='C' AND PED_VEN.CO_COMPANIA = PAG_PED.CO_COMPANIA AND PED_VEN.CO_LOCAL = PAG_PED.CO_LOCAL AND PED_VEN.NU_PEDIDO = PAG_PED.NU_PEDIDO AND PAG_PED.CO_COMPANIA = FOR_PAG.CO_COMPANIA(+) AND PAG_PED.CO_FORMA_PAGO = FOR_PAG.CO_FORMA_PAGO(+) AND FOR_PAG.CO_FORMA_PAGO_PADRE IS NULL GROUP BY FOR_PAG.CO_FORMA_PAGO, FOR_PAG.DE_CORTA_FORMA_PAGO UNION SELECT TO_CHAR(NVL(FOR_PAG_PADRE.CO_FORMA_PAGO,' ')) CO_FORMA_PAGO, TO_CHAR(NVL(FOR_PAG_PADRE.DE_CORTA_FORMA_PAGO,' ')) DE_CORTA_FORMA_PAGO, SUM(NVL(PAG_PED.IM_TOTAL_PAGO,0)-NVL(PAG_PED.VA_VUELTO,0)) TOTAL FROM VTTC_PEDIDO_VENTA PED_VEN, VTTX_FORMA_PAGO_PEDIDO PAG_PED, VTTR_FORMA_PAGO FOR_PAG, VTTR_FORMA_PAGO FOR_PAG_PADRE
1 = 1
AND PED_VEN.CO_COMPANIA = #{localId.coCompania}
AND PED_VEN.CO_LOCAL = #{localId.coLocal}
AND PED_VEN.FE_PEDIDO BETWEEN TO_DATE(TO_CHAR(#{feInicio},'dd/MM/yyyy') || ' 00:00:00','dd/MM/yyyy HH24:MI:SS') AND TO_DATE(TO_CHAR(#{feFin},'dd/MM/yyyy') || ' 23:59:59','dd/MM/yyyy HH24:MI:SS')
AND PED_VEN.ES_PEDIDO_VENTA ='C' AND PED_VEN.CO_COMPANIA = PAG_PED.CO_COMPANIA AND PED_VEN.CO_LOCAL = PAG_PED.CO_LOCAL AND PED_VEN.NU_PEDIDO = PAG_PED.NU_PEDIDO AND PAG_PED.CO_COMPANIA = FOR_PAG.CO_COMPANIA(+) AND PAG_PED.CO_FORMA_PAGO = FOR_PAG.CO_FORMA_PAGO(+) AND FOR_PAG.CO_FORMA_PAGO_PADRE IS NOT NULL AND FOR_PAG.CO_COMPANIA = FOR_PAG_PADRE.CO_COMPANIA AND FOR_PAG.CO_FORMA_PAGO_PADRE = FOR_PAG_PADRE.CO_FORMA_PAGO GROUP BY FOR_PAG_PADRE.CO_FORMA_PAGO, FOR_PAG_PADRE.DE_CORTA_FORMA_PAGO) Z

SELECT PED_VEN.NU_PEDIDO nuPedido, PED_VEN.FE_PEDIDO fePedido, PROD.CO_PRODUCTO coProducto, DECODE(PED_VEN.ES_PEDIDO_VENTA,'C','E','') emitido, PROD.DE_CORTA_PRODUCTO deProducto, PROD.DE_UNIDAD_PRODUCTO unidadProducto, DET_PED_VEN.CA_ATENDIDA caAtendida, DET_PED_VEN.VA_VENTA vaVenta, DET_PED_VEN.VA_PRECIO_PUBLICO vaPrecioPublico, DET_PED_VEN.VA_PRECIO_VENTA*(1/(TO_NUMBER(NVL(pc_impuesto_1,0))/100+1)) vaPrecioVentaSinIgv, DET_PED_VEN.VA_PRECIO_VENTA vaPrecioVenta, DET_PED_VEN.VA_PRECIO_PROMEDIO vaPrecioPromedio, (NVL(DET_PED_VEN.PC_DESCUENTO_1,0)/100)*NVL(DET_PED_VEN.CA_ATENDIDA,0)*NVL(DET_PED_VEN.VA_VENTA,0) vaDescuento, DET_PED_VEN.TI_COMPROBANTE tiComprobante, DET_PED_VEN.NU_COMPROBANTE_PAGO nuComprobante, TO_CHAR(SUBSTR( NVL(DET_PED_VEN.NU_COMPROBANTE_PAGO,''),1,3)||'-'|| SUBSTR( NVL(DET_PED_VEN.NU_COMPROBANTE_PAGO,''),4,7) )|| NVL(DECODE(DET_PED_VEN.TI_COMPROBANTE,'02','F',' '),' ')nuComprobanteCustom, CTL.DE_LABORATORIO deLaboratorio FROM VTTC_PEDIDO_VENTA PED_VEN, VTTD_PEDIDO_VENTA DET_PED_VEN, LGTM_PRODUCTO PROD, CMTR_LABORATORIO CTL
1 = 1
AND PED_VEN.CO_COMPANIA = #{localId.coCompania}
AND PED_VEN.CO_LOCAL = #{localId.coLocal}
AND DET_PED_VEN.TI_COMPROBANTE = #{tipoDocumento}
AND PED_VEN.FE_PEDIDO BETWEEN TO_DATE(TO_CHAR(#{feInicio},'dd/MM/yyyy') || ' 00:00:00','dd/MM/yyyy HH24:MI:SS') AND TO_DATE(TO_CHAR(#{feFin},'dd/MM/yyyy') || ' 23:59:59','dd/MM/yyyy HH24:MI:SS')
AND PED_VEN.CO_COMPANIA=DET_PED_VEN.CO_COMPANIA AND PED_VEN.CO_LOCAL=DET_PED_VEN.CO_LOCAL AND PED_VEN.NU_PEDIDO=DET_PED_VEN.NU_PEDIDO AND DET_PED_VEN.CO_COMPANIA=PROD.CO_COMPANIA(+) AND DET_PED_VEN.CO_PRODUCTO=PROD.CO_PRODUCTO(+) AND CTL.CO_LABORATORIO=PROD.CO_LABORATORIO AND PED_VEN.ES_PEDIDO_VENTA='C' AND PED_VEN.FE_ANULA_PEDIDO IS NULL

SELECT PED_VEN.NU_PEDIDO nuPedido, SUBSTR(COMP_PAGO.NU_COMPROBANTE_PAGO,1,3)||'-'|| SUBSTR(COMP_PAGO.NU_COMPROBANTE_PAGO,4,7)|| NVL(DECODE(COMP_PAGO.TI_COMPROBANTE,'02','F'),' ') nuComprobante, PED_VEN.FE_PEDIDO fePedido, NVL(COMP_PAGO.NO_IMPRESO_CLIENTE,' ') noCliente, COMP_PAGO.TI_COMPROBANTE tiComprobante, PED_VEN.VA_TOTAL_IMPUESTO imp, CU.ID_USUARIO noVendedor, (PED_VEN.VA_TOTAL_VENTA ) vaPrecioPromedio, (comp_pago.VA_TOTAL_PRECIO_VENTA) vaPrecioVenta, PED_VEN.ES_PEDIDO_VENTA esPedidoVenta FROM VTTC_PEDIDO_VENTA PED_VEN , VTTV_COMPROBANTE_PAGO COMP_PAGO, CMTS_USUARIO CU
1 = 1
AND PED_VEN.CO_COMPANIA = #{localId.coCompania}
AND PED_VEN.CO_LOCAL = #{localId.coLocal}
AND PED_VEN.FE_PEDIDO BETWEEN TO_DATE(TO_CHAR(#{feInicio},'dd/MM/yyyy') || ' 00:00:00','dd/MM/yyyy HH24:MI:SS') AND TO_DATE(TO_CHAR(#{feFin},'dd/MM/yyyy') || ' 23:59:59','dd/MM/yyyy HH24:MI:SS')
AND PED_VEN.ES_PEDIDO_VENTA = 'C' AND PED_VEN.CO_COMPANIA = COMP_PAGO.CO_COMPANIA AND PED_VEN.CO_LOCAL = COMP_PAGO.CO_LOCAL AND PED_VEN.NU_PEDIDO = COMP_PAGO.NU_PEDIDO_ANUL AND CU.CO_COMPANIA= PED_VEN.CO_COMPANIA AND CU.CO_LOCAL= PED_VEN.CO_LOCAL AND CU.NU_SEC_USUARIO= PED_VEN.CO_VENDEDOR AND PED_VEN.FE_ANULA_PEDIDO IS NOT NULL UNION SELECT PED_VEN.NU_PEDIDO nuPedido, SUBSTR(COMP_PAGO.NU_COMPROBANTE_PAGO,1,3)||'-'|| SUBSTR(COMP_PAGO.NU_COMPROBANTE_PAGO,4,7)|| NVL(DECODE(COMP_PAGO.TI_COMPROBANTE,'02','F'),' ') nuComprobante, PED_VEN.FE_PEDIDO fePedido, NVL(COMP_PAGO.NO_IMPRESO_CLIENTE,' ') noCliente, COMP_PAGO.TI_COMPROBANTE tiComprobante, PED_VEN.VA_TOTAL_IMPUESTO imp, CU.ID_USUARIO noVendedor, (PED_VEN.VA_TOTAL_VENTA ) vaPrecioPromedio, (comp_pago.VA_TOTAL_PRECIO_VENTA) vaPrecioVenta, PED_VEN.ES_PEDIDO_VENTA esPedidoVenta FROM VTTC_PEDIDO_VENTA PED_VEN , VTTV_COMPROBANTE_PAGO COMP_PAGO, CMTS_USUARIO CU
1 = 1
AND PED_VEN.CO_COMPANIA = #{localId.coCompania}
AND PED_VEN.CO_LOCAL = #{localId.coLocal}
AND PED_VEN.FE_PEDIDO BETWEEN TO_DATE(TO_CHAR(#{feInicio},'dd/MM/yyyy') || ' 00:00:00','dd/MM/yyyy HH24:MI:SS') AND TO_DATE(TO_CHAR(#{feFin},'dd/MM/yyyy') || ' 23:59:59','dd/MM/yyyy HH24:MI:SS')
AND PED_VEN.ES_PEDIDO_VENTA = 'C' AND PED_VEN.CO_COMPANIA = COMP_PAGO.CO_COMPANIA AND PED_VEN.CO_LOCAL = COMP_PAGO.CO_LOCAL AND PED_VEN.NU_PEDIDO = COMP_PAGO.NU_PEDIDO AND CU.CO_COMPANIA= PED_VEN.CO_COMPANIA AND CU.CO_LOCAL= PED_VEN.CO_LOCAL AND CU.NU_SEC_USUARIO= PED_VEN.CO_VENDEDOR UNION SELECT PED_VEN.NU_PEDIDO , ' ' , PED_VEN.FE_PEDIDO , '' , '', 0, CU.ID_USUARIO noVendedor, 0, 0, PED_VEN.ES_PEDIDO_VENTA esPedidoVenta FROM VTTC_PEDIDO_VENTA PED_VEN, CMTR_MOTIVO MOTIVO, CMTS_USUARIO CU
1 = 1
AND PED_VEN.CO_COMPANIA = #{localId.coCompania}
AND PED_VEN.CO_LOCAL = #{localId.coLocal}
AND PED_VEN.FE_PEDIDO BETWEEN TO_DATE(TO_CHAR(#{feInicio},'dd/MM/yyyy') || ' 00:00:00','dd/MM/yyyy HH24:MI:SS') AND TO_DATE(TO_CHAR(#{feFin},'dd/MM/yyyy') || ' 23:59:59','dd/MM/yyyy HH24:MI:SS')
AND PED_VEN.ES_PEDIDO_VENTA ='P' AND PED_VEN.CO_GRUPO_MOTIVO=MOTIVO.CO_GRUPO_MOTIVO AND PED_VEN.CO_MOTIVO_ANULACION=MOTIVO.CO_MOTIVO AND CU.CO_COMPANIA= PED_VEN.CO_COMPANIA AND CU.CO_LOCAL= PED_VEN.CO_LOCAL AND CU.NU_SEC_USUARIO= PED_VEN.CO_VENDEDOR UNION SELECT PED_VEN.NU_PEDIDO , ' ' , PED_VEN.FE_PEDIDO , '' , '', 0, CU.ID_USUARIO noVendedor, 0, 0, PED_VEN.ES_PEDIDO_VENTA esPedidoVenta FROM VTTC_PEDIDO_VENTA PED_VEN, CMTS_USUARIO CU
1 = 1
AND PED_VEN.CO_COMPANIA = #{localId.coCompania}
AND PED_VEN.CO_LOCAL = #{localId.coLocal}
AND PED_VEN.FE_PEDIDO BETWEEN TO_DATE(TO_CHAR(#{feInicio},'dd/MM/yyyy') || ' 00:00:00','dd/MM/yyyy HH24:MI:SS') AND TO_DATE(TO_CHAR(#{feFin},'dd/MM/yyyy') || ' 23:59:59','dd/MM/yyyy HH24:MI:SS')
AND PED_VEN.ES_PEDIDO_VENTA ='N' AND CU.CO_COMPANIA= PED_VEN.CO_COMPANIA AND CU.CO_LOCAL= PED_VEN.CO_LOCAL AND CU.NU_SEC_USUARIO= PED_VEN.CO_VENDEDOR

SELECT DET_PED_VEN.CO_PRODUCTO coProducto, DET_PED_VEN.pc_impuesto_1 vaImpuesto, PROD.DE_CORTA_PRODUCTO deProducto, PROD.DE_UNIDAD_PRODUCTO unidadProducto, CTL.DE_LABORATORIO deLaboratorio, SUM(NVL(DET_PED_VEN.CA_ATENDIDA, 0) / DECODE(DET_PED_VEN.VA_FRACCION, 0, 1,DET_PED_VEN.VA_FRACCION )) caAtendida, SUM(NVL(DET_PED_VEN.CA_ATENDIDA, 0) * (NVL(DET_PED_VEN.VA_VENTA,0))) vaVenta, SUM(NVL(DET_PED_VEN.CA_ATENDIDA, 0) * (NVL(DET_PED_VEN.VA_VENTA,0) - (NVL(DET_PED_VEN.VA_VENTA, 0)* (DET_PED_VEN.PC_DESCUENTO_1/100)))) vaPrecioVenta, SUM( DECODE(PROD.CO_MONEDA, '01', NVL(PROD.VA_PRECIO_PROMEDIO, 0) * NVL(DET_PED_VEN.CA_ATENDIDA, 0) / DECODE(NVL(DET_PED_VEN.VA_FRACCION, 0), 0, 1, DET_PED_VEN.VA_FRACCION), '02', NVL(PROD.VA_PRECIO_PROMEDIO, 0) * NVL(DET_PED_VEN.CA_ATENDIDA, 0) / DECODE(NVL(DET_PED_VEN.VA_FRACCION, 0), 0, 1, DET_PED_VEN.VA_FRACCION) * NVL(PED_VEN.VA_TASA_CAMBIO, 0), 0)) vaPrecioPromedio FROM VTTC_PEDIDO_VENTA PED_VEN, VTTD_PEDIDO_VENTA DET_PED_VEN, LGTM_PRODUCTO PROD, CMTR_LABORATORIO CTL
1 = 1
AND PED_VEN.CO_COMPANIA = #{localId.coCompania}
AND PED_VEN.CO_LOCAL = #{localId.coLocal}
AND PED_VEN.FE_PEDIDO BETWEEN TO_DATE(TO_CHAR(#{feInicio},'dd/MM/yyyy') || ' 00:00:00','dd/MM/yyyy HH24:MI:SS') AND TO_DATE(TO_CHAR(#{feFin},'dd/MM/yyyy') || ' 23:59:59','dd/MM/yyyy HH24:MI:SS')
AND PED_VEN.ES_PEDIDO_VENTA ='C' AND PED_VEN.CO_COMPANIA = DET_PED_VEN.CO_COMPANIA AND PED_VEN.CO_LOCAL = DET_PED_VEN.CO_LOCAL AND PED_VEN.NU_PEDIDO = DET_PED_VEN.NU_PEDIDO AND DET_PED_VEN.CO_COMPANIA = PROD.CO_COMPANIA(+) AND DET_PED_VEN.CO_PRODUCTO = PROD.CO_PRODUCTO(+) AND CTL.CO_LABORATORIO=PROD.CO_LABORATORIO GROUP BY DET_PED_VEN.CO_PRODUCTO, DET_PED_VEN.pc_impuesto_1, PROD.DE_CORTA_PRODUCTO , DET_PED_VEN.VA_PRECIO_PROMEDIO, PROD.VA_PRECIO_COMPRA, PROD.DE_UNIDAD_PRODUCTO, CTL.DE_LABORATORIO , DET_PED_VEN.VA_FRACCION, PROD.CO_MONEDA, PED_VEN.VA_TASA_CAMBIO, DET_PED_VEN.IN_PRODUCTO_FRACCION
