mapper.select:
SELECT LPL.CO_COMPANIA coCompania , LPL.CO_PRODUCTO coProducto , LP.DE_PRODUCTO deProducto, CL.DE_LABORATORIO deLaboratorio, LP.DE_UNIDAD_PRODUCTO unidadProducto, LPL.DE_UNIDAD_FRACCION unidadFraccion, DECODE(LP.CO_MONEDA,'01','S/.','$') moneda, LPL.VA_PRECIO_PUBLICO precioVentaPublico, VI.PC_IMPUESTO vaImpuesto, (NVL(LPL.VA_VENTA,0) - (NVL(LPL.VA_VENTA,0) * (1-(1-(NVL(LPL.PC_DESCUENTO_1,0)/100))))) precioVentaSinIGV, LPL.PC_DESCUENTO_1 porcentajeDescuento, DECODE(LPL.IN_PROD_FRACCIONADO, 'S', TRUNC(LPL.CA_STOCK_DISPONIBLE/LPL.VA_FRACCION), LPL.CA_STOCK_DISPONIBLE) stkEntero, DECODE(LPL.IN_PROD_FRACCIONADO, 'S', MOD(LPL.CA_STOCK_DISPONIBLE, LPL.VA_FRACCION), 0) stkFraccion , LPL.VA_VENTA precioProveedor, LPL.VA_COSTO_PRODUCTO precioPromedio , LPL.VA_TOTAL_INVENTARIO precioValorizado , DECODE(nvl(LPL.VA_VENTA,0),0,0,(NVL(LPL.VA_VENTA,0) - (NVL(LPL.VA_VENTA,0) * (1-(1-(NVL(LPL.PC_DESCUENTO_1,0)/100)))))*100/NVL(LPL.VA_VENTA,0)) porcentajeMargen , DECODE(nvl(LPL.VA_VENTA,0),0,0,LPL.VA_VENTA/(1+VI.PC_IMPUESTO/100)) precioPVP , DECODE(LPL.VA_VENTA,NULL,0,LPL.VA_VENTA) - (DECODE(LPL.VA_VENTA,NULL,0,LPL.VA_VENTA) * (1-(1-(DECODE(LPL.PC_DESCUENTO_1,NULL,0,LPL.PC_DESCUENTO_1)/100)))) precioPVPFinal, LPL.VA_FRACCION vaFraccion, LPL.IN_PROD_INVENTARIO inProdInventario, LPL.ES_PROD_LOCAL esProductoLocal, LLPE.DE_LINEA_PROD_ERP lineaG1, LGPE.DE_GRUPO_PROD_ERP divisionG2, LGA.DE_GRUPO_ANATOMICO subdivisionG3, LGT.DE_GRUPO_TERAPEUTICO familiaG4, LAT.DE_ACCION_TERAPEUTICA subFamiliaG5 FROM LGTR_PRODUCTO_LOCAL LPL LEFT JOIN LGTM_PRODUCTO LP ON LPL.CO_COMPANIA = LP.CO_COMPANIA AND LPL.CO_PRODUCTO = LP.CO_PRODUCTO AND LPL.NU_REVISION_PRODUCTO = LP.NU_REVISION_PRODUCTO INNER JOIN VTTR_IMPUESTO VI ON LP.CO_COMPANIA = VI.CO_COMPANIA AND LP.CO_IMPUESTO_1 = VI.CO_IMPUESTO LEFT JOIN CMTR_LABORATORIO CL ON CL.CO_COMPANIA = LP.CO_COMPANIA AND CL.CO_LABORATORIO=LP.CO_LABORATORIO LEFT JOIN LGTR_ACCION_TERAPEUTICA LAT ON LAT.CO_ACCION_TERAPEUTICA=LP.CO_ACCION_TERAPEUTICA LEFT JOIN LGTR_GRUPO_TERAPEUTICO LGT ON LGT.CO_GRUPO_TERAPEUTICO=LP.CO_GRUPO_TERAPEUTICO LEFT JOIN LGTR_GRUPO_ANATOMICO LGA ON LGA.CO_GRUPO_ANATOMICO=LP.CO_GRUPO_ANATOMICO LEFT JOIN LGTR_GRUPO_PROD_ERP LGPE ON LGPE.CO_GRUPO_PROD_ERP=LP.CO_GRUPO_PROD_ERP LEFT JOIN LGTR_LINEA_PROD_ERP LLPE ON LLPE.CO_LINEA_PROD_ERP=LP.CO_LINEA_PROD_ERP
1 = 1
AND LPL.CO_COMPANIA= #{localId.coCompania}
AND LPL.CO_LOCAL = #{localId.coLocal}
AND CL.CO_LABORATORIO = #{coLaboratorio}
AND ( LP.CO_PRODUCTO = #{buscar} or LP.DE_PRODUCTO LIKE UPPER(CONCAT(CONCAT('%',#{buscar}),'%')) )

SELECT LPL.CO_COMPANIA coCompania, LPL.CO_PRODUCTO coProducto, LP.DE_PRODUCTO deProducto, LPL.IN_PROD_FRACCIONADO inProdFraccionado, LPL.DE_UNIDAD_FRACCION unidadFraccion, LPL.ES_PROD_LOCAL esProductoLocal, LP.DE_UNIDAD_PRODUCTO unidadProducto, (DECODE(LPL.VA_VENTA,NULL,0,LPL.VA_VENTA) - (DECODE(LPL.VA_VENTA,NULL,0,LPL.VA_VENTA) * (1-(1-(DECODE(LPL.PC_DESCUENTO_1,NULL,0,LPL.PC_DESCUENTO_1)/100))))) precioVenta, LPL.FE_ULT_MOD_PRECIO feModificacion , SYSDATE - LPL.FE_ULT_MOD_PRECIO nroDiasSinCambioPrecio , CL.DE_LABORATORIO deLaboratorio FROM LGTR_PRODUCTO_LOCAL LPL LEFT JOIN LGTM_PRODUCTO LP ON LPL.CO_COMPANIA = LP.CO_COMPANIA AND LPL.CO_PRODUCTO = LP.CO_PRODUCTO AND LPL.NU_REVISION_PRODUCTO = LP.NU_REVISION_PRODUCTO LEFT JOIN CMTR_LABORATORIO CL ON CL.CO_COMPANIA = LP.CO_COMPANIA AND CL.CO_LABORATORIO=LP.CO_LABORATORIO
1 = 1 AND LPL.CO_COMPANIA= #{localId.coCompania} AND LPL.CO_LOCAL = #{localId.coLocal}
AND ( LP.CO_PRODUCTO = #{buscar} or LP.DE_PRODUCTO LIKE UPPER(CONCAT(CONCAT('%',#{buscar}),'%')) )

SELECT LP.CO_COMPANIA coCompania, LP.CO_PRODUCTO coProducto, LP.NU_REVISION_PRODUCTO nuRevision, LP.DE_PRODUCTO deProducto, LPL.IN_PROD_FRACCIONADO inProdFraccionado, LP.DE_UNIDAD_PRODUCTO unidadProducto, LPL.DE_UNIDAD_FRACCION unidadFraccion, LPL.VA_VENTA precioVenta, LPL.VA_COSTO_PRODUCTO precioPromedio , DECODE(nvl(LPL.VA_VENTA,0),0,0,(NVL(LPL.VA_VENTA,0) - (NVL(LPL.VA_VENTA,0) * (1-(1-(NVL(LPL.PC_DESCUENTO_1,0)/100)))))*100/NVL(LPL.VA_VENTA,0)) porcentajeMargen , DECODE(nvl(LPL.VA_VENTA,0),0,0,LPL.VA_VENTA/(1+VI.PC_IMPUESTO/100)) precioPVP , DECODE(LPL.VA_VENTA,NULL,0,LPL.VA_VENTA) - (DECODE(LPL.VA_VENTA,NULL,0,LPL.VA_VENTA) * (1-(1-(DECODE(LPL.PC_DESCUENTO_1,NULL,0,LPL.PC_DESCUENTO_1)/100)))) precioPVPFinal, VPB.FE_CREA_PROD_BONO feCreacion, VPB.FE_INICIO_BONO feInicio, VPB.FE_FIN_BONO feFin, VPB.VA_BONO vaIncentivo, VPB.NU_SEC_PROD_BONO nuSecProdBono, VPB.NU_REVISION_PRODUCTO nuRevisionProducto FROM LGTR_PRODUCTO_LOCAL LPL LEFT JOIN LGTM_PRODUCTO LP ON LPL.CO_COMPANIA = LP.CO_COMPANIA AND LPL.CO_PRODUCTO = LP.CO_PRODUCTO AND LPL.NU_REVISION_PRODUCTO = LP.NU_REVISION_PRODUCTO INNER JOIN VTTR_IMPUESTO VI ON LP.CO_COMPANIA = VI.CO_COMPANIA AND LP.CO_IMPUESTO_1 = VI.CO_IMPUESTO LEFT JOIN VTTR_PRODUCTO_BONO VPB ON VPB.CO_COMPANIA = LP.CO_COMPANIA AND VPB.CO_PRODUCTO = LP.CO_PRODUCTO AND VPB.ES_PROD_BONO = 'A'
1 = 1 AND LPL.CO_COMPANIA= #{localId.coCompania} AND LPL.CO_LOCAL = #{localId.coLocal}
AND ( LP.CO_PRODUCTO = #{buscar} or LP.DE_PRODUCTO LIKE UPPER(#{buscar}) )
AND LP.CO_LABORATORIO = #{coLaboratorio}

SELECT LP.CO_COMPANIA coCompania , LP.CO_PRODUCTO coProducto , LP.DE_PRODUCTO deProducto, CL.DE_LABORATORIO deLaboratorio, LP.DE_UNIDAD_PRODUCTO unidadProducto, LP.IN_PROD_FRACCIONABLE inProdFraccionado, LP.MIN_FRACC minFraccion , LP.MAX_FRACC maxFraccion, LP.CA_EMPAQUE_MIN caEmpaqueMin , LP.CA_EMPAQUE_MAX caEmpaqueMax FROM LGTM_PRODUCTO LP LEFT JOIN CMTR_LABORATORIO CL ON CL.CO_COMPANIA = LP.CO_COMPANIA AND CL.CO_LABORATORIO=LP.CO_LABORATORIO
1 = 1
AND LP.CO_COMPANIA= #{localId.coCompania}
AND CL.CO_LABORATORIO = #{coLaboratorio}
AND ( LP.CO_PRODUCTO = #{buscar} or LP.DE_PRODUCTO LIKE UPPER(CONCAT(CONCAT('%',#{buscar}),'%')) )

SELECT LF.CO_COMPANIA coCompania, LF.CO_LOCAL coLocal, LF.CO_PRODUCTO coProducto, MIN(LF.FE_VIGENCIA) feVigencia, MIN(LF.FE_CREA) feCreacion, MIN(LF.FE_CREA) feUltCambio FROM LGTM_PRODUCTO LP INNER JOIN LGTR_FRACCION LF ON LP.CO_COMPANIA= LF.CO_COMPANIA AND LP.CO_PRODUCTO= LF.CO_PRODUCTO
1 = 1
AND LP.CO_COMPANIA= #{localId.coCompania}
AND CL.CO_LABORATORIO = #{coLaboratorio}
AND LP.CO_PRODUCTO = #{coProducto}
AND ( LP.CO_PRODUCTO = #{buscar} or LP.DE_PRODUCTO LIKE UPPER(CONCAT(CONCAT('%',#{buscar}),'%')) )
GROUP BY LF.CO_COMPANIA, LF.CO_LOCAL, LF.CO_PRODUCTO

        SELECT
        count(*)
        FROM
        LGTR_FRACCION VPB
        WHERE
        VPB.CO_COMPANIA=  #{coCompania}
        AND VPB.CO_LOCAL   =  #{coLocal}
        AND VPB.CO_PRODUCTO   =  #{coProducto}

mapper.update:
UPDATE
    LGTR_FRACCION
SET
    ES_FRACCION='I',
    IN_ACTIVO='0',
    FE_MOD=SYSDATE,
    ID_MOD=#{usuario}
WHERE
    CO_COMPANIA=  #{coCompania}
    AND CO_LOCAL   =  #{coLocal}
    AND CO_PRODUCTO   =  #{coProducto}
